<template>  <div class="card flex justify-center">    <Menu :model="sideBarItems" :pt="{      root : {        class: 'rounded-none p-2'      },      separator: {        class: 'my-2'      }    }" class="w-full md:w-60">      <template #start>        <span class="flex items-center gap-1 px-2 py-5 justify-center">            <span class="text-2xl font-semibold">File <span class="text-primary">Storage</span></span>        </span>        <div class="flex items-center mt-3">          <Button aria-controls="overlay_menu" aria-haspopup="true" class="px-4 py-2 ml-3" icon="pi pi-plus" label="New"                  raised variant="outlined" @click="toggleNew" />          <Menu id="overlay_menu" ref="newOp" :model="newPopoverItems" :popup="true" :pt=" {            submenuLabel:  {              class: 'py-0'            }          }" />        </div>      </template>      <template #item="{ item, props }">        <a v-ripple class="flex items-center" v-bind="props.action">          <span :class="item.icon" />          <span>{{ item.label }}</span>          <Badge v-if="item.badge" :value="item.badge" class="ml-auto" />          <span v-if="item.shortcut"                class="ml-auto border border-surface rounded bg-emphasis text-muted-color text-xs p-1">            {{ item.shortcut }}          </span>        </a>      </template>    </Menu>    <FolderDialog v-model="showFolderDialog"/>  </div></template><script lang="ts" setup>import { ref } from 'vue'import FolderDialog from '@/components/FolderDialog.vue'import type { MenuItemCommandEvent } from 'primevue/menuitem'const showFolderDialog = ref<boolean>();const showFileDialog = ref<boolean>();const showAlbumDialog = ref<boolean>();const sideBarItems = ref([  {    items: [      {        label: 'Home',        icon: 'pi pi-home'      },      {        label: 'My Storage',        icon: 'pi pi-folder'      },      {        label: 'Album',        icon: 'pi pi-image'      }    ]  },  {    separator: true  }])const POPOVER = {  FILE: 'file',  FOLDER: 'folder',  ALBUM: 'album'};const onClickItem = (event: MenuItemCommandEvent) => {  const key = event.item.key;  if (key === POPOVER.FILE) {    showFileDialog.value = true;  } else if (key === POPOVER.FOLDER) {    showFolderDialog.value = true;    console.log(showFolderDialog);  } else if (key === POPOVER.ALBUM) {    showAlbumDialog.value = true;  }}const newPopoverItems = ref([  {    items: [      {        label: 'Upload File',        icon: 'pi pi-file-arrow-up',        key: POPOVER.FILE,        command: onClickItem      },      {        label: 'New Folder',        icon: 'pi pi-folder-plus',        key: POPOVER.FOLDER,        command: onClickItem      },      {        label: 'New Album',        icon: 'pi pi-images',        key: POPOVER.ALBUM,        command: onClickItem      }    ]  }])const newOp = ref()const toggleNew = (event: MouseEvent) => {  newOp.value.toggle(event)}const selectedOption = ref(null)</script><style scoped></style>